local trackData={};Tabs.Home:Toggle({Title="–û—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏–µ –ø—É–ª—å",Default=false,Callback=function(s)trackData.enabled=s;if not s then if trackData.workspaceConn then trackData.workspaceConn:Disconnect()end;for _,c in pairs(trackData.bulletConns or{})do c:Disconnect()end;trackData.bulletConns={};return end;local function getTarget(pos)local t,d=nil,math.huge;for _,p in ipairs(Players:GetPlayers())do if p~=LP then local char=p.Character;local h=char and char:FindFirstChildOfClass("Humanoid");local rp=char and char:FindFirstChild("HumanoidRootPart");if h and h.Health>0 and rp then local dist=(pos-rp.Position).Magnitude;if dist<=70 and dist<d then d=dist;t=rp end end end end;return t end;local function attachTrack(b)local bp=b:IsA("BasePart")and b or b:FindFirstChildWhichIsA("BasePart");if not bp then return end;local bv=bp:FindFirstChildOfClass("BodyVelocity");if not bv then bv=Instance.new("BodyVelocity");bv.MaxForce=Vector3.new(math.huge,math.huge,math.huge);bv.Velocity=bp.Velocity;bv.Parent=bp end;local conn=RunService.Heartbeat:Connect(function()if not bp.Parent or not trackData.enabled then conn:Disconnect();trackData.bulletConns[conn]=nil;return end;local t=getTarget(bp.Position);if t then local dir=(t.Position-bp.Position).Unit;bv.Velocity=dir*bp.Velocity.Magnitude;bp.CFrame=CFrame.new(bp.Position,t.Position)end end);trackData.bulletConns[conn]=true end;trackData.workspaceConn=workspace.ChildAdded:Connect(function(c)local isB=(c.Name:lower():find("bullet")or c.Name:lower():find("projectile")or c.Name:lower():find("missile"))and(c:FindFirstChild("Owner")and c.Owner.Value==LP);if isB then task.wait(0.05);attachTrack(c)end end)end})
